// page
function hideShow(){
    const showMe = document.querySelector('#page');

   if (showMe.style.display === "none"){
    showMe.style.display = 'block'
   }
   else{
    showMe.style.display = 'none';
   }

}

// page1
function hideShow1() {
   const showMe1 = document.querySelector('#page1');

   if (showMe1.style.display === 'none') {
    showMe1.style.display = 'block';
    loop:false;
   }
   else{
    showMe1.style.display = 'none'
   }

}

